mapper.select:
INSERT INTO CMS_ROLE_PERMISSION ( ID, ROLE_ID, PERMISSION_ID, STATUS, CREATE_TIME, UPDATE_TIME ) select seq_CMS_ROLE_PERMISSION.nextval,p.* from (
SELECT #{item.permission.roleId} roleId,#{item.role.permissionId} permissionId, #{item.permission.status } status,SYSDATE CREATE_TIME,SYSDATE UPDATE_TIME FROM DUAL
)p
